import React, { useState } from 'react';
import axios from 'axios';

function ArticleForm() {
	const [form, setForm] = useState({ codigo: '', descripcion: '', precio: '' });
	const [errors, setErrors] = useState({});
	const [success, setSuccess] = useState('');
	const [loading, setLoading] = useState(false);

	// Validación en tiempo real
	const validate = (field, value) => {
		let error = '';
		if (field === 'codigo') {
			if (!value.trim()) error = 'El código es obligatorio.';
			else if (!/^\d+$/.test(value)) error = 'Solo números.';
		}
		if (field === 'descripcion') {
			if (!value.trim()) error = 'La descripción es obligatoria.';
			else if (value.length < 2) error = 'Mínimo 2 caracteres.';
		}
		if (field === 'precio') {
			if (!value.trim()) error = 'El precio es obligatorio.';
			else if (!/^\d+(\.\d{1,2})?$/.test(value)) error = 'Formato inválido. Ej: 123 o 123.45';
		}
		return error;
	};

	const handleChange = e => {
		const { name, value } = e.target;
		setForm({ ...form, [name]: value });
		setErrors({ ...errors, [name]: validate(name, value) });
		setSuccess('');
	};

	const handleSubmit = async e => {
		e.preventDefault();
		let newErrors = {};
		Object.keys(form).forEach(field => {
			const error = validate(field, form[field]);
			if (error) newErrors[field] = error;
		});
		setErrors(newErrors);
		if (Object.keys(newErrors).length > 0) return;
		setLoading(true);
		try {
			await axios.post('/api/articulos/', form);
			setSuccess('Artículo creado exitosamente.');
			setForm({ codigo: '', descripcion: '', precio: '' });
			setErrors({});
			setTimeout(() => window.location.reload(), 1200);
		} catch (err) {
			setSuccess('');
			setErrors({ submit: 'Error al crear artículo.' });
		}
		setLoading(false);
	};

	const handleClear = () => {
		setForm({ codigo: '', descripcion: '', precio: '' });
		setErrors({});
		setSuccess('');
	};

	return (
		<div className="form-create-wrapper">
			<h2 className="form-title">
				<span role="img" aria-label="crear" style={{marginRight:8}}>📝</span>
				Crear artículo
			</h2>
			<form onSubmit={handleSubmit} className="form-create-form" autoComplete="off">
				<div className="input-group">
					<input
						name="codigo"
						type="text"
						placeholder="Código"
						value={form.codigo}
						onChange={handleChange}
						required
						autoComplete="off"
						className={errors.codigo ? 'input-error' : ''}
						aria-label="Código"
						title="Solo números"
					/>
					{errors.codigo && <span className="error-tooltip">{errors.codigo}</span>}
				</div>
				<div className="input-group">
					<input
						name="descripcion"
						type="text"
						placeholder="Descripción"
						value={form.descripcion}
						onChange={handleChange}
						required
						autoComplete="off"
						className={errors.descripcion ? 'input-error' : ''}
						aria-label="Descripción"
						title="Mínimo 2 caracteres"
					/>
					{errors.descripcion && <span className="error-tooltip">{errors.descripcion}</span>}
				</div>
				<div className="input-group">
					<input
						name="precio"
						type="text"
						placeholder="Precio (ej: 123 o 123.45)"
						value={form.precio}
						onChange={handleChange}
						required
						autoComplete="off"
						className={errors.precio ? 'input-error' : ''}
						aria-label="Precio"
						title="Ingrese un número entero o decimal, máximo dos decimales"
					/>
					{errors.precio && <span className="error-tooltip">{errors.precio}</span>}
				</div>
				<button type="submit" className="form-panel-button" disabled={loading}>
					{loading ? 'Creando...' : 'Crear'}
				</button>
				<button type="button" className="form-panel-button clear-btn" onClick={handleClear} disabled={loading}>
					Limpiar
				</button>
			</form>
			{success && <div className="success-message">{success}</div>}
			{errors.submit && <div className="error-message">{errors.submit}</div>}
		</div>
	);
}

export default ArticleForm;
