import React, { useEffect, useState } from "react";
import axios from "axios";

import ArticleList from "./components/ArticleList";
import ArticleForm from "./components/ArticleForm";
import ImportExcel from "./components/ImportExcel";
import ExportExcel from "./components/ExportExcel";

import "./App.css";

function App() {
	const [articulos, setArticulos] = useState([]);

	useEffect(() => {
		axios
			.get("/api/articulos/")
			.then((res) => setArticulos(res.data))
			.catch((err) => console.error(err));
	}, []);

	return (
		<div className="app-container">
			<header className="app-header">
				<div className="header-content">
					<span className="header-icon" role="img" aria-label="box">
						üì¶
					</span>
					<h1>Gesti√≥n de Art√≠culos</h1>
				</div>
			</header>

			<main>
				<div className="front-grid">
					<div className="form-panel">
						<ArticleForm />
						<ImportExcel />
					</div>
					<div className="data-panel-horizontal">
						<div className="table-area-horizontal">
							<h2 className="list-title">Lista de Art√≠culos</h2>
							<ArticleList articulos={articulos} />
						</div>
						<div className="export-area-horizontal">
							<ExportExcel data={articulos} />
						</div>
					</div>
				</div>
			</main>

			<footer className="app-footer">
				&copy; {new Date().getFullYear()} Gesti√≥n de Art√≠culos | Desaf√≠o Fullstack
			</footer>
		</div>
	);
}

export default App;
