import React from 'react';

function ArticleList({ articulos }) {
	return (
		<div className="table-wrapper">
			<table className="article-table">
				<thead>
					<tr>
						<th>Código</th>
						<th>Descripción</th>
						<th>Precio</th>
					</tr>
				</thead>
				<tbody>
					{articulos.map(a => (
						<tr key={a.codigo}>
							<td>{a.codigo}</td>
							<td>{a.descripcion}</td>
							<td>{a.precio}</td>
						</tr>
					))}
				</tbody>
			</table>
		</div>
	);
}
export default ArticleList;
